# alert dashboard reports to use the new, dashboard_report_view
UPDATE indicatorTemplate SET
template = '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<indicatorTemplateXML>\n	<author>Colcamex Resources Inc. for Oscar BC</author>\n	<uid></uid>\n	<heading>\n		<category>Billing</category>\n		<subCategory>CDM Incentive Program</subCategory>\n		<name>Diabetes Mellitus (G14050)</name>\n		<definition>Annual chronic care incentive for full service most responsible general practitioner.</definition>\n		<framework>GP Services Committee: Chronic Disease Management Incentives</framework>\n		<frameworkVersion>01-01-2016</frameworkVersion>\n		<notes>\n			Full details here: https://sgp.bc.ca/fee/diabetes-chronic-care-incentive-copy/			\n		</notes>\n	</heading>\n	<indicatorQuery>\n		<version>10-01-2017</version>\n		<params>\n			<!-- \n				Required parameter provider. Value options are: \n					[ an individual providerNo, or provider range ] ie: value=\"370, 1001\" \n					\"all\" ie: value=\"all\" including null.\n					\"loggedInProvider\" ie:\n				Default is \"loggedInProvider\"\n				Use this parameter in the query as ${provider}\n				This parameter should be used for fetching patient\'s assigned to a MRP.\n				ie: WHERE demographic.provider_no = ${provider}\n			-->\n			<parameter id=\"provider\" name=\"provider_no\" value=\"loggedInProvider\" />\n			<parameter id=\"pstatus\" name=\"patient_status\" value=\"\'%AC%\'\" />\n			<parameter id=\"daysanniversary\" name=\"days_anniversary\" value=\"365\" />\n			<parameter id=\"today\" name=\"today\" value=\"DATE(NOW())\" />\n			<parameter id=\"visitsqualifier\" name=\"visits_qualifier\" value=\"2\" />\n			<parameter id=\"billingStatus\" name=\"billing_status\" value=\"\'%S%\'\" />\n			<parameter id=\"excludeBillingStatus\" name=\"billing_status\" value=\"%D%,%R%,%F%,%N%\" />\n						\n			<parameter id=\"bonusBillingcode\" name=\"billing_code\" value=\"14050\" />			\n			<parameter id=\"phoneBillingcode\" name=\"billing_code\" value=\"14076,14079\" />	\n			<parameter id=\"acpBillingcode\" name=\"billing_code\" value=\"14029\" />		\n			<parameter id=\"visitBillingcode\" name=\"billing_code\" value=\"12100,00100,15300,16100,17100,18100,\n				12101,00101,15301,16101,17101,18101,12120,00120,15320,16120,17120,18120,00103,\n				14090,14091,14094,00114,00115\" />				\n			<parameter id=\"excludeBillingcode\" name=\"billing_code\" value=\"14063\" />							\n			<parameter id=\"excludeDxCode\" name=\"dxresearch_code\" value=\"\'\'\" />\n			<parameter id=\"dxcode\" name=\"dxresearch_code\" value=\"250\" />\n			\n			<parameter id=\"dxcodestatus\" name=\"status\" value=\"\'%A%\'\" />\n			<parameter id=\"patientNameExclude\" name=\"last_name\" value=\"TEST%,BOOP,INTEGRATOR%\" />	\n		</params>\n		\n        <range>\n            <upperLimit id=\"groupBillingcode\" label=\"Group Visit Upper\" name=\"Group Visit Upper\" value=\"13781\" />\n            <lowerLimit id=\"groupBillingcode\" label=\"Group Visit Lower\" name=\"Group Visit Lower\" value=\"13763\" />\n        </range>\n\n		<query>\n			<!-- Indicator SQL Query here -->\n			SELECT			 \n				ROUND( IFNULL( SUM( CASE\n					WHEN d.demographic_no &gt; 0\n					THEN 1\n					ELSE 0\n				END ), 0), 1 ) AS \"Patients up to date\",\n			\n				ROUND( IFNULL( SUM( CASE\n				WHEN ( ABS( DATEDIFF( dxr.start_date, ${today} ) ) &gt;= ${daysanniversary} )  \n						AND ( IFNULL(BILL.service_date, 0) = 0 )\n						AND ( (IFNULL(EXCLUDE_DX.code1,0) = 0) AND (IFNULL(EXCLUDE_DX.code2,0) = 0) )  \n				THEN CASE \n							WHEN IFNULL(VISITS.qualifier,0) = ${visitsqualifier}\n							THEN 1\n							WHEN ( IFNULL(VISITS.qualifier,0) + IFNULL(PHONE.qualifier,0) ) = ${visitsqualifier}\n							THEN 1\n							WHEN ( IFNULL(VISITS.qualifier,0) + IFNULL(GVISIT.qualifier,0) ) = ${visitsqualifier}\n							THEN 1\n							WHEN ( IFNULL(VISITS.qualifier,0) + IFNULL(ACPVISIT.qualifier,0) ) = ${visitsqualifier}\n							THEN 1\n							ELSE 0\n					 END\n				ELSE 0 \n				END ), 0), 1) AS \"Patients billable\",\n			\n				ROUND( IFNULL( SUM( CASE\n				WHEN ( ABS( DATEDIFF( dxr.start_date, ${today} ) ) &gt;= ${daysanniversary} )  \n						AND ( IFNULL(BILL.service_date, 0) = 0 )\n						AND ( (IFNULL(EXCLUDE_DX.code1,0) = 0) AND (IFNULL(EXCLUDE_DX.code2,0) = 0) ) \n						AND IFNULL(VISITS.qualifier,0) &lt; ${visitsqualifier}\n						AND IFNULL(VISITS.qualifier,0) + IFNULL(PHONE.qualifier,0) &lt; ${visitsqualifier}\n						AND IFNULL(VISITS.qualifier,0) + IFNULL(GVISIT.qualifier,0) &lt; ${visitsqualifier}\n						AND IFNULL(VISITS.qualifier,0) + IFNULL(ACPVISIT.qualifier,0) &lt; ${visitsqualifier}\n				THEN 1\n				ELSE 0\n				END ), 0), 1) AS \"Patients recall\" \n\n			FROM demographic d \n			INNER JOIN dxresearch dxr \n			ON ( d.demographic_no = dxr.demographic_no ) \n				LEFT JOIN (\n					SELECT bm.demographic_no, bm.billingstatus, bm.billing_code, bm.service_date\n					FROM dashboard_report_view bm\n					INNER JOIN (\n						SELECT MAX(service_date) AS service, demographic_no AS demographic\n						FROM dashboard_report_view\n						WHERE billing_code = ${bonusBillingcode}\n						AND billingstatus NOT IN ${excludeBillingStatus}\n						AND DATE(service_date) BETWEEN DATE_SUB( ${today}, INTERVAL ${daysanniversary} DAY ) AND ${today}\n						GROUP BY demographic_no\n					) current\n					ON ( current.demographic = bm.demographic_no AND current.service = bm.service_date ) \n					WHERE bm.billing_code = ${bonusBillingcode}\n				) BILL\n				ON(BILL.demographic_no = d.demographic_no)\n				LEFT JOIN (\n					SELECT 	COUNT(demographic_no) AS \'count\',\n							CASE \n								WHEN COUNT(demographic_no) = 1\n								THEN 1\n								WHEN COUNT(demographic_no) > 1\n								THEN 2\n								ELSE 0\n							END AS \'qualifier\',\n							demographic_no, \n							MAX(service_date) AS service\n					FROM dashboard_report_view\n					WHERE billingstatus LIKE ${billingStatus}\n					AND DATE(service_date) BETWEEN DATE_SUB( ${today}, INTERVAL ${daysanniversary} DAY ) AND ${today}\n					AND billing_code IN ${visitBillingcode} \n					GROUP BY demographic_no\n				) VISITS\n				ON (VISITS.demographic_no = d.demographic_no)\n				LEFT JOIN (\n					SELECT 	COUNT(demographic_no) AS \'count\', \n							IF( COUNT(demographic_no) > 0, 1, 0) AS \'qualifier\',\n							demographic_no, \n							MAX(service_date) AS service\n					FROM dashboard_report_view\n					WHERE billingstatus LIKE ${billingStatus}\n					AND DATE( service_date ) BETWEEN DATE_SUB( ${today}, INTERVAL ${daysanniversary} DAY ) AND ${today}	\n					AND billing_code IN ${phoneBillingcode} \n					GROUP BY demographic_no\n				) PHONE\n				ON (PHONE.demographic_no = d.demographic_no)\n				LEFT JOIN (\n					SELECT 	COUNT(demographic_no) AS \'count\', \n							IF( COUNT(demographic_no) > 0, 1, 0) AS \'qualifier\',\n							demographic_no, \n							MAX(service_date) AS service\n					FROM dashboard_report_view\n					WHERE billingstatus LIKE ${billingStatus}\n					AND DATE(service_date) BETWEEN DATE_SUB( ${today}, INTERVAL ${daysanniversary} DAY ) AND ${today}\n					AND billing_code BETWEEN ${lowerLimit.groupBillingcode} AND ${upperLimit.groupBillingcode}	\n					GROUP BY demographic_no\n				) GVISIT\n				ON ( GVISIT.demographic_no = d.demographic_no)\n				LEFT JOIN (\n					SELECT 	COUNT(demographic_no) AS \'count\', \n							IF( COUNT(demographic_no) > 0, 1, 0) AS \'qualifier\',\n							demographic_no, \n							MAX(service_date) AS service\n					FROM dashboard_report_view\n					WHERE billingstatus LIKE ${billingStatus}\n					AND DATE(service_date) BETWEEN DATE_SUB( ${today}, INTERVAL ${daysanniversary} DAY ) AND ${today}\n					AND billing_code = ${acpBillingcode}\n					GROUP BY demographic_no\n				) ACPVISIT\n				ON ( ACPVISIT.demographic_no = d.demographic_no)\n				LEFT JOIN (\n					SELECT 	IFNULL( MIN(dxresearch_code), 0) AS code1, \n							IF( IFNULL( MIN(dxresearch_code), 0) != IFNULL( MAX(dxresearch_code),0 ), MAX(dxresearch_code), 0) AS code2, \n							demographic_no\n					FROM dxresearch\n					WHERE dxresearch_code LIKE ${excludeDxCode}\n					AND `status` LIKE ${dxcodestatus} \n					GROUP BY demographic_no\n				) EXCLUDE_DX\n				ON ( EXCLUDE_DX.demographic_no = d.demographic_no)\n				LEFT JOIN (\n					SELECT billing_code, demographic_no\n					FROM dashboard_report_view bm\n					WHERE billingstatus LIKE ${billingStatus}\n					AND billing_code LIKE ${excludeBillingcode}	\n					GROUP BY demographic_no HAVING COUNT(demographic_no) > -1\n				) EXCLUDE_BILL\n				ON(EXCLUDE_BILL.demographic_no = d.demographic_no )\n			WHERE d.patient_status LIKE ${pstatus} \n			AND dxr.dxresearch_code LIKE ${dxcode} \n			AND dxr.`status` LIKE ${dxcodestatus} \n			AND d.demographic_no > 0 \n			AND d.last_name NOT IN ${patientNameExclude}\n			AND d.provider_no LIKE ${provider}			\n		</query>\n	</indicatorQuery>\n	<drillDownQuery>\n		<version>10-01-2017</version>\n		<params>\n			<parameter id=\"provider\" name=\"provider_no\" value=\"loggedInProvider\" />\n			<parameter id=\"pstatus\" name=\"patient_status\" value=\"\'%AC%\'\" />\n			<parameter id=\"daysanniversary\" name=\"days_anniversary\" value=\"365\" />\n			<parameter id=\"today\" name=\"today\" value=\"DATE(NOW())\" />\n			<parameter id=\"visitsqualifier\" name=\"visits_qualifier\" value=\"2\" />\n			<parameter id=\"billingStatus\" name=\"billing_status\" value=\"\'%S%\'\" />\n			<parameter id=\"excludeBillingStatus\" name=\"billing_status\" value=\"%D%,%R%,%F%,%N%\" />			\n			<parameter id=\"bonusBillingcode\" name=\"billing_code\" value=\"14050\" />			\n			<parameter id=\"phoneBillingcode\" name=\"billing_code\" value=\"14076,14079\" />	\n			<parameter id=\"acpBillingcode\" name=\"billing_code\" value=\"14029\" />		\n			<parameter id=\"visitBillingcode\" name=\"billing_code\" value=\"12100,00100,15300,16100,17100,18100,\n				12101,00101,15301,16101,17101,18101,12120,00120,15320,16120,17120,18120,00103,\n				14090,14091,14094,00114,00115\" />				\n			<parameter id=\"excludeBillingcode\" name=\"billing_code\" value=\"14063\" />							\n			<parameter id=\"excludeDxCode\" name=\"dxresearch_code\" value=\"\'\'\" />\n			<parameter id=\"dxcode\" name=\"dxresearch_code\" value=\"250\" />\n			<parameter id=\"dxcodestatus\" name=\"status\" value=\"\'%A%\'\" />\n			<parameter id=\"patientNameExclude\" name=\"last_name\" value=\"TEST%,BOOP,INTEGRATOR%\" />	\n		</params>\n			\n		<range>\n			<upperLimit id=\"groupBillingcode\" label=\"Group Visit Upper\" name=\"Group Visit Upper\" value=\"13781\" />\n			<lowerLimit id=\"groupBillingcode\" label=\"Group Visit Lower\" name=\"Group Visit Lower\" value=\"13763\" />\n		</range>\n		\n		<displayColumns>\n			<column id=\"demographicNo\" name=\"d.demographic_no\" title=\"Id\" primary=\"true\" />\n			<column id=\"name\" name=\"CONCAT( d.last_name, \', \',d.first_name )\" title=\"Name\" primary=\"false\" />\n			<column id=\"age\" name=\"FLOOR( ABS( DATEDIFF( DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), NOW() ) ) / 365.25 )\" title=\"Age\" primary=\"false\" />\n			<column id=\"gender\" name=\"d.sex\" title=\"Gender\" primary=\"false\" />	\n					\n			<column id=\"billStatus\" name=\"CASE WHEN IFNULL(EXCLUDE_BILL.billing_code,0) &gt; 0\n				THEN \'palliative\' \n				WHEN ( ABS( DATEDIFF( dxr.start_date, ${today} ) ) &gt;= ${daysanniversary} ) AND ( IFNULL(BILL.service_date, 0) = 0 ) AND ( (IFNULL(EXCLUDE_DX.code1,0) = 0) AND (IFNULL(EXCLUDE_DX.code2,0) = 0) ) \n				THEN CASE \n							WHEN IFNULL(VISITS.qualifier,0) = ${visitsqualifier}\n							THEN \'billable\'\n							WHEN ( IFNULL(VISITS.qualifier,0) + IFNULL(PHONE.qualifier,0) ) = ${visitsqualifier}\n							THEN \'billable\'\n							WHEN ( IFNULL(VISITS.qualifier,0) + IFNULL(GVISIT.qualifier,0) ) = ${visitsqualifier}\n							THEN \'billable\'\n							WHEN ( IFNULL( VISITS.qualifier, 0 ) + IFNULL( ACPVISIT.qualifier, 0 ) ) = ${visitsqualifier}\n							THEN \'billable\'\n							ELSE \'recall\'\n					 END\n				ELSE \'-\'\n				END\" title=\"CDM Status\" primary=\"false\" />	\n				\n			<column id=\"lastBillDate\" name=\"IFNULL( DATE_FORMAT( DATE(BILL.service_date), \'%m-%d-%Y\' ), 0)\" title=\"Last Bill\" primary=\"false\" />				\n			<column id=\"dxDate\" name=\"DATE_FORMAT( dxr.start_date , \'%m-%d-%Y\' )\" title=\"Dx Date\" primary=\"false\" />			\n			<column id=\"visitDate\" name=\"IFNULL( DATE_FORMAT(VISITS.service, \'%m-%d-%Y\' ), \'\')\" title=\"Last MD Visit\" primary=\"false\" />\n			<column id=\"phoneDate\" name=\"IFNULL(DATE_FORMAT(PHONE.service, \'%m-%d-%Y\' ), \'\')\" title=\"Last Phone\" primary=\"false\" /> \n			<column id=\"groupDate\" name=\"IFNULL(DATE_FORMAT(GVISIT.service, \'%m-%d-%Y\' ), \'\')\" title=\"Last Grp Visit\" primary=\"false\" />\n			<column id=\"acpDate\" name=\"IFNULL(DATE_FORMAT(ACPVISIT.service, \'%m-%d-%Y\' ), \'\')\" title=\"Last ACP Visit\" primary=\"false\" />\n			\n		</displayColumns>\n		<exportColumns>\n 			<column id=\"demographicNo\" name=\"d.demographic_no\" title=\"Id\" primary=\"true\" />\n			<column id=\"name\" name=\"CONCAT( d.last_name, \', \',d.first_name )\" title=\"Name\" primary=\"false\" />\n			<column id=\"age\" name=\"FLOOR( ABS( DATEDIFF( DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), NOW() ) ) / 365.25 )\" title=\"Age\" primary=\"false\" />\n			<column id=\"gender\" name=\"d.sex\" title=\"Gender\" primary=\"false\" />	\n					\n			<column id=\"billStatus\" name=\"CASE WHEN IFNULL(EXCLUDE_BILL.billing_code,0) &gt; 0\n				THEN \'palliative\' \n				WHEN ( ABS( DATEDIFF( dxr.start_date, ${today} ) ) &gt;= ${daysanniversary} ) AND ( IFNULL(BILL.service_date, 0) = 0 ) AND ( (IFNULL(EXCLUDE_DX.code1,0) = 0) AND (IFNULL(EXCLUDE_DX.code2,0) = 0) ) \n				THEN CASE \n							WHEN IFNULL(VISITS.qualifier,0) = ${visitsqualifier}\n							THEN \'billable\'\n							WHEN ( IFNULL(VISITS.qualifier,0) + IFNULL(PHONE.qualifier,0) ) = ${visitsqualifier}\n							THEN \'billable\'\n							WHEN ( IFNULL(VISITS.qualifier,0) + IFNULL(GVISIT.qualifier,0) ) = ${visitsqualifier}\n							THEN \'billable\'\n							WHEN ( IFNULL( VISITS.qualifier, 0 ) + IFNULL( ACPVISIT.qualifier, 0 ) ) = ${visitsqualifier}\n							THEN \'billable\'\n							ELSE \'recall\'\n					 END\n				ELSE \'-\'\n				END\" title=\"CDM Status\" primary=\"false\" />	\n				\n			<column id=\"lastBillDate\" name=\"IFNULL( DATE_FORMAT( DATE(BILL.service_date), \'%m-%d-%Y\' ), 0)\" title=\"Last Bill\" primary=\"false\" />				\n			<column id=\"dxDate\" name=\"DATE_FORMAT( dxr.start_date , \'%m-%d-%Y\' )\" title=\"Dx Date\" primary=\"false\" />			\n			<column id=\"visitDate\" name=\"IFNULL( DATE_FORMAT(VISITS.service, \'%m-%d-%Y\' ), \'\')\" title=\"Last MD Visit\" primary=\"false\" />\n			<column id=\"phoneDate\" name=\"IFNULL(DATE_FORMAT(PHONE.service, \'%m-%d-%Y\' ), \'\')\" title=\"Last Phone\" primary=\"false\" /> \n			<column id=\"groupDate\" name=\"IFNULL(DATE_FORMAT(GVISIT.service, \'%m-%d-%Y\' ), \'\')\" title=\"Last Grp Visit\" primary=\"false\" />\n			<column id=\"groupDate\" name=\"IFNULL(DATE_FORMAT(ACPVISIT.service, \'%m-%d-%Y\' ), \'\')\" title=\"Last ACP Visit\" primary=\"false\" />			\n		</exportColumns>\n		<query>\n			SELECT *\n			FROM demographic d \n			INNER JOIN dxresearch dxr \n			ON ( d.demographic_no = dxr.demographic_no ) \n				LEFT JOIN (\n					SELECT bm.demographic_no, bm.billingstatus, bm.billing_code, bm.service_date\n					FROM dashboard_report_view bm\n					INNER JOIN (\n						SELECT MAX(service_date) AS service, demographic_no AS demographic\n						FROM dashboard_report_view \n						WHERE billing_code = ${bonusBillingcode}\n						AND billingstatus NOT IN ${excludeBillingStatus}\n						AND DATE(service_date) BETWEEN DATE_SUB( ${today}, INTERVAL ${daysanniversary} DAY ) AND ${today}\n						GROUP BY demographic_no\n					) current\n					ON ( current.demographic = bm.demographic_no AND current.service = bm.service_date ) \n					WHERE bm.billing_code = ${bonusBillingcode}\n				) BILL\n				ON(BILL.demographic_no = d.demographic_no)\n				LEFT JOIN (\n					SELECT 	COUNT(demographic_no) AS \'count\',\n							CASE \n								WHEN COUNT(demographic_no) = 1\n								THEN 1\n								WHEN COUNT(demographic_no) &gt; 1\n								THEN 2\n								ELSE 0\n							END AS \'qualifier\',\n							demographic_no, \n							MAX(service_date) AS service\n					FROM dashboard_report_view \n					WHERE billingstatus LIKE ${billingStatus}\n					AND DATE(service_date) BETWEEN DATE_SUB( ${today}, INTERVAL ${daysanniversary} DAY ) AND ${today}\n					AND billing_code IN ${visitBillingcode} \n					GROUP BY demographic_no\n				) VISITS\n				ON (VISITS.demographic_no = d.demographic_no)\n				LEFT JOIN (\n					SELECT 	COUNT(demographic_no) AS \'count\', \n							IF( COUNT(demographic_no) &gt; 0, 1, 0) AS \'qualifier\',\n							demographic_no, \n							MAX(service_date) AS service\n					FROM dashboard_report_view \n					WHERE billingstatus LIKE ${billingStatus}\n					AND DATE( service_date ) BETWEEN DATE_SUB( ${today}, INTERVAL ${daysanniversary} DAY ) AND ${today}	\n					AND billing_code IN ${phoneBillingcode} \n					GROUP BY demographic_no\n				) PHONE\n				ON (PHONE.demographic_no = d.demographic_no)\n				LEFT JOIN (\n					SELECT 	COUNT(demographic_no) AS \'count\', \n							IF( COUNT(demographic_no) &gt; 0, 1, 0) AS \'qualifier\',\n							demographic_no, \n							MAX(service_date) AS service\n					FROM dashboard_report_view \n					WHERE billingstatus LIKE ${billingStatus}\n					AND DATE(service_date) BETWEEN DATE_SUB( ${today}, INTERVAL ${daysanniversary} DAY ) AND ${today}\n					AND billing_code BETWEEN ${lowerLimit.groupBillingcode} AND ${upperLimit.groupBillingcode}	\n					GROUP BY demographic_no\n				) GVISIT\n				ON ( GVISIT.demographic_no = d.demographic_no)\n				LEFT JOIN (\n					SELECT 	COUNT(demographic_no) AS \'count\', \n							IF( COUNT(demographic_no) &gt; 0, 1, 0) AS \'qualifier\',\n							demographic_no, \n							MAX(service_date) AS service\n					FROM dashboard_report_view\n					WHERE billingstatus LIKE ${billingStatus}\n					AND DATE(service_date) BETWEEN DATE_SUB( ${today}, INTERVAL ${daysanniversary} DAY ) AND ${today}\n					AND billing_code = ${acpBillingcod