OUNT(demographic_no) = 1\n								THEN 1\n								WHEN COUNT(demographic_no) > 1\n								THEN 2\n								ELSE 0\n							END AS \'qualifier\',\n							demographic_no, \n							MAX(service_date) AS service\n					FROM dashboard_report_view\n					WHERE billingstatus LIKE ${billingStatus}\n					AND DATE(service_date) BETWEEN DATE_SUB( ${today}, INTERVAL ${daysanniversary} DAY ) AND ${today}\n					AND billing_code IN ${visitBillingcode} \n					GROUP BY demographic_no\n				) VISITS\n				ON (VISITS.demographic_no = d.demographic_no)\n				LEFT JOIN (\n					SELECT 	COUNT(demographic_no) AS \'count\', \n							IF( COUNT(demographic_no) > 0, 1, 0) AS \'qualifier\',\n							demographic_no, \n							MAX(service_date) AS service\n					FROM dashboard_report_view\n					WHERE billingstatus LIKE ${billingStatus}\n					AND DATE( service_date ) BETWEEN DATE_SUB( ${today}, INTERVAL ${daysanniversary} DAY ) AND ${today}	\n					AND billing_code IN ${phoneBillingcode} \n					GROUP BY demographic_no\n				) PHONE\n				ON (PHONE.demographic_no = d.demographic_no)\n				LEFT JOIN (\n					SELECT 	COUNT(demographic_no) AS \'count\', \n							IF( COUNT(demographic_no) > 0, 1, 0) AS \'qualifier\',\n							demographic_no, \n							MAX(service_date) AS service\n					FROM dashboard_report_view\n					WHERE billingstatus LIKE ${billingStatus}\n					AND DATE(service_date) BETWEEN DATE_SUB( ${today}, INTERVAL ${daysanniversary} DAY ) AND ${today}\n					AND billing_code BETWEEN ${lowerLimit.groupBillingcode} AND ${upperLimit.groupBillingcode}	\n					GROUP BY demographic_no\n				) GVISIT\n				ON ( GVISIT.demographic_no = d.demographic_no)\n				LEFT JOIN (\n					SELECT 	COUNT(demographic_no) AS \'count\', \n							IF( COUNT(demographic_no) > 0, 1, 0) AS \'qualifier\',\n							demographic_no, \n							MAX(service_date) AS service\n					FROM dashboard_report_view\n					WHERE billingstatus LIKE ${billingStatus}\n					AND DATE(service_date) BETWEEN DATE_SUB( ${today}, INTERVAL ${daysanniversary} DAY ) AND ${today}\n					AND billing_code = ${acpBillingcode}\n					GROUP BY demographic_no\n				) ACPVISIT\n				ON ( ACPVISIT.demographic_no = d.demographic_no)\n				LEFT JOIN (\n					SELECT 	IFNULL( MIN(dxresearch_code), 0) AS code1, \n							IF( IFNULL( MIN(dxresearch_code), 0) != IFNULL( MAX(dxresearch_code),0 ), MAX(dxresearch_code), 0) AS code2, \n							demographic_no\n					FROM dxresearch\n					WHERE dxresearch_code IN ${excludeDxCode}\n					AND `status` LIKE ${dxcodestatus} \n					GROUP BY demographic_no\n				) EXCLUDE_DX\n				ON ( EXCLUDE_DX.demographic_no = d.demographic_no)\n				LEFT JOIN (\n					SELECT billing_code, demographic_no\n					FROM dashboard_report_view bm\n					WHERE billingstatus LIKE ${billingStatus}\n					AND billing_code LIKE ${excludeBillingcode}	\n					GROUP BY demographic_no HAVING COUNT(demographic_no) > -1\n				) EXCLUDE_BILL\n				ON(EXCLUDE_BILL.demographic_no = d.demographic_no )\n			WHERE d.patient_status LIKE ${pstatus} \n			AND dxr.dxresearch_code LIKE ${dxcode} \n			AND dxr.`status` LIKE ${dxcodestatus} \n			AND d.demographic_no > 0 \n			AND d.last_name NOT IN ${patientNameExclude}\n			AND d.provider_no LIKE ${provider}\n			ORDER BY `CDM Status` DESC				\n		</query>\n	</drillDownQuery>\n	<shared>false</shared>\n	<sharedMetricSetName></sharedMetricSetName>\n	<sharedMetricDataId></sharedMetricDataId>\n	<sharedMappings>\n		<sharedMapping fromLabel=\"\" toLabel=\"\"/>\n	</sharedMappings>\n</indicatorTemplateXML>\n'
