template = '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<indicatorTemplateXML>\n	<author>Colcamex Resources Inc. for Oscar BC</author>\n	<uid></uid>\n	<heading>\n		<category>Billing</category>\n		<subCategory>Total/Year: 14066, 14076, 14078 </subCategory>\n		<name>Email/Text/Telephone (14078) – 200/yr</name>\n		<definition>Limit 200 Email/Text/Telephone Medical Advice Relay Fee (14078) billings available per MD per calendar year</definition>\n		<framework>GP Services Committee</framework>\n		<frameworkVersion>01-01-2016</frameworkVersion>\n		<notes>\n			Details here: https://sgp.bc.ca/fee/personal-health-risk-assessment/\n		</notes>\n	</heading>\n	<indicatorQuery>\n		<version>27-11-2017</version>\n		<params>\n			<!-- \n				Required parameter provider. Value options are: \n					[ an individual providerNo, or provider range ] ie: value=\"370, 1001\" \n					\"all\" ie: value=\"all\" including null.\n					\"loggedInProvider\" ie:\n				Default is \"loggedInProvider\"\n				Use this parameter in the query as ${provider}\n				This parameter should be used for fetching patient\'s assigned to a MRP.\n				ie: WHERE demographic.provider_no = ${provider}\n			-->\n			<parameter id=\"provider\" name=\"provider_no\" value=\"loggedInProvider\" />\n			<parameter id=\"billingcode\" name=\"billing_code\" value=\"14078\" />\n			<parameter id=\"billinglimit\" name=\"billing_limit\" value=\"200\" />\n			<parameter id=\"status\" name=\"billingstatus\" value=\"\'S\'\" />\n		</params>\n\n		<query>\n			SELECT \n				ROUND( IFNULL( SUM( IF(bm.billingmaster_no IS NOT NULL AND bm.billingmaster_no > 0, 1, 0) ), 0),1) AS \'# Billed\',	\n				ROUND( ${billinglimit} - IFNULL( SUM( IF(bm.billingmaster_no IS NOT NULL AND bm.billingmaster_no > 0, 1, 0) ), 0),1 ) AS \'# Remaining\'\n			FROM dashboard_report_view bm\n			LEFT JOIN provider p\n			ON(p.ohip_no = bm.practitioner_no)\n			WHERE billing_code = ${billingcode}\n			AND YEAR(NOW()) = YEAR(createdate)\n			AND billingstatus LIKE ${status}\n			AND p.provider_no IS NOT NULL\n			AND p.provider_no = ${provider}\n		</query>\n	</indicatorQuery>\n	<drillDownQuery>\n		<version>27-11-2017</version>\n		<params>\n			<parameter id=\"provider\" name=\"provider_no\" value=\"loggedInProvider\" />\n			<parameter id=\"billingcode\" name=\"billing_code\" value=\"14078\" />\n			<parameter id=\"billinglimit\" name=\"billing_limit\" value=\"200\" />\n			<parameter id=\"status\" name=\"billingstatus\" value=\"\'S\'\" />\n		</params>\n\n		<displayColumns>\n			<column id=\"demographicNo\" name=\"d.demographic_no\" title=\"Id\" primary=\"true\" />\n			<column id=\"name\" name=\"CONCAT( d.last_name, \', \',d.first_name )\" title=\"Name\" primary=\"false\" />\n			<column id=\"age\" name=\"FLOOR( ABS( DATEDIFF( DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), NOW() ) ) / 365.25 )\" title=\"Age\" primary=\"false\" />\n			<column id=\"gender\" name=\"d.sex\" title=\"Gender\" primary=\"false\" />\n			<column id=\"lastbill\" name=\"IFNULL( DATE_FORMAT( DATE(bm.service_date), \'%m-%d-%Y\' ), 0)\" title=\"Last Bill (m-d-y)\" primary=\"false\" />\n			<column id=\"billingcode\" name=\"bm.billing_code\" title=\"Billing Code\" primary=\"false\" />\n			<column id=\"payee\" name=\"bm.payee_no\" title=\"Payee\" primary=\"false\" />\n			<column id=\"billingprovider\" name=\"bm.practitioner_no\" title=\"Billing Provider\" primary=\"false\" />\n		</displayColumns>\n		<exportColumns>\n			<column id=\"demographicNo\" name=\"d.demographic_no\" title=\"Id\" primary=\"true\" />\n			<column id=\"name\" name=\"CONCAT( d.last_name, \', \',d.first_name )\" title=\"Name\" primary=\"false\" />\n			<column id=\"age\" name=\"FLOOR( ABS( DATEDIFF( DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), NOW() ) ) / 365.25 )\" title=\"Age\" primary=\"false\" />\n			<column id=\"gender\" name=\"d.sex\" title=\"Gender\" primary=\"false\" />\n			<column id=\"lastbill\" name=\"IFNULL( DATE_FORMAT( DATE(bm.service_date), \'%m-%d-%Y\' ), 0)\" title=\"Last Bill (m-d-y)\" primary=\"false\" />\n			<column id=\"billingcode\" name=\"bm.billing_code\" title=\"Billing Code\" primary=\"false\" />\n			<column id=\"payee\" name=\"bm.payee_no\" title=\"Payee\" primary=\"false\" />\n			<column id=\"billingprovider\" name=\"bm.practitioner_no\" title=\"Billing Provider\" primary=\"false\" />\n		</exportColumns>\n		\n		<query>\n			SELECT *\n			FROM dashboard_report_view bm\n			LEFT JOIN provider p\n			ON ( p.ohip_no = bm.practitioner_no )\n			JOIN demographic d\n			ON ( d.demographic_no = bm.demographic_no )\n			WHERE billing_code = ${billingcode}\n			AND YEAR( NOW() ) = YEAR( createdate )\n			AND billingstatus LIKE ${status}\n			AND p.provider_no = ${provider}\n		</query>\n	</drillDownQuery>\n	<shared>false</shared>\n	<sharedMetricSetName></sharedMetricSetName>\n	<sharedMetricDataId></sharedMetricDataId>\n	<sharedMappings>\n		<sharedMapping fromLabel=\"\" toLabel=\"\"/>\n	</sharedMappings>\n</indicatorTemplateXML>'
