=\"NOW()\" />\n			<lowerLimit id=\"labdateObs\" label=\"From Date\" name=\"Date\" value=\"DATE_SUB( NOW(), INTERVAL 5 YEAR )\" />\n			<upperLimit id=\"labdateObs\" label=\"Date Today\" name=\"Date\" value=\"NOW()\" />\n		</range>\n		\n		<displayColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true\" />\n			<column id=\"name\" name=\"CONCAT( d.last_name, \', \', d.first_name )\" title=\"Patient Name\" primary=\"false\" />\n			<column id=\"dob\" name=\"DATE_FORMAT( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth), \'%m-%d-%Y\' )\" title=\"Date of Birth (mm-dd-yy)\" primary=\"false\" />\n			<column id=\"age\" name=\"TIMESTAMPDIFF(YEAR, DATE( CONCAT(d.year_of_birth,\'-\',d.month_of_birth,\'-\',d.date_of_birth) ), CURDATE() )\" title=\"Age\" primary=\"false\" />\n			<column id=\"billings\" name=\"IFNULL( BILLING.billcount, \'\')\" title=\"Billings\" primary=\"false\" />\n			<column id=\"visits\" name=\"IFNULL( VISIT.visitcount, \'\')\" title=\"Visits\" primary=\"false\" />\n			<column id=\"lastOBS1\" name=\"IFNULL( OBS1LAST.result, \'\')\" title=\"HbA1C\" primary=\"false\" />\n			<column id=\"testDateOBS1\" name=\"IFNULL( DATE_FORMAT( OBS1LAST.dateObserved, \'%m-%d-%Y\' ), \'\')\" title=\"HbA1C Test Date (mm-dd-yyyy)\" primary=\"false\" />\n			<column id=\"numOBS1s\" name=\"IFNULL( OBS1CNT.countOBS, \'\')\" title=\"Num. HbA1C &gt;= 6.5% Last 5yrs\" primary=\"false\" />\n			<column id=\"lastOBS2\" name=\"IFNULL( OBS2LAST.result, \'\')\" title=\"FBS\" primary=\"false\" />\n			<column id=\"testDateOBS2\" name=\"IFNULL( DATE_FORMAT( OBS2LAST.dateObserved, \'%m-%d-%Y\' ), \'\')\" title=\"FBS Test Date (mm-dd-yyyy)\" primary=\"false\" />\n                        <column id=\"numOfMeds\" name=\"IFNULL( MEDS.numOfMeds, \'\')\" title=\"Diabetes Medications\" primary=\"false\" />\n		</displayColumns>\n		\n		<exportColumns>\n			<column id=\"demographic\" name=\"d.demographic_no\" title=\"Patient Id\" primary=\"true