SELECT DATE_FORMAT(start_time, '%H:%i') AS start_time
FROM appointment
WHERE appointment_no=${appt_no}


SELECT DATE_FORMAT(ADDTIME(DATE_FORMAT(end_time, '%H:%i:00'), '00:01:00'), '%H:%i') AS rounded_endtime_plus_1_minute
FROM appointment
WHERE appointment_no=${appt_no}

SELECT location
FROM appointment
