SELECT GROUP_CONCAT(CONCAT(p.date_prescribed,' ',SUBSTRING_INDEX(p.textView, '

', -1)),' 
' ORDER BY p.date_prescribed DESC SEPARATOR '') AS rx FROM prescription p WHERE p.demographic_no=${demographic}

SELECT cmn.note AS note FROM casemgmt_note cmn
			JOIN (SELECT DISTINCT a.uuid FROM casemgmt_note a
			WHERE a.demographic_no=${demographic}
			ORDER BY a.observation_date DESC LIMIT 1)
			AS A ON (cmn.uuid = A.uuid)
			WHERE cmn.demographic_no=${demographic}
			ORDER BY update_date
			DESC LIMIT 1;

SELECT CONCAT('G',c_gravida,' T', c_term,' P' , c_prem,' A',
			c_abort ,' L', c_living) as onGTPAL FROM formONAREnhancedRecord f
			where demographic_no=${demographic} order by ID desc limit 1;

SELECT bcar.c_EDD FROM
			(select id, demographic_no, c_EDD, formEdited from formBCAR2007 UNION select id, demographic_no, c_EDD, formEdited from formBCAR2012) as bcar
			where bcar.demographic_no=${demographic} order by bcar.formEdited desc limit 1;

SELECT DATE_FORMAT(bcar.c_EDD, '%d/%m/%Y') as c_EDD FROM
			(select id, demographic_no, c_EDD, formEdited from formBCAR2007 UNION select id, demographic_no, c_EDD, formEdited from formBCAR2012) as bcar
			where bcar.demographic_no=${demographic} order by bcar.formEdited desc limit 1;

SELECT EXTRACTVALUE(provider.comments, '/xml_p_billinggroup_no') AS billing_group_no FROM provider
INNER JOIN demographic ON demographic.provider_no=provider.provider_no
WHERE demographic.demographic_no=${demographic}

SELECT SUBSTR(p.comments,
			LOCATE('<xml_p_fax>', p.comments )+11,
			LOCATE('</xml_p_fax>', p.comments )-(LOCATE('<xml_p_fax>', p.comments )+11)
			) as fax
			FROM provider p JOIN demographic d ON(d.provider_no=p.provider_no)
			WHERE d.demographic_no = ${demographic};

SELECT p.last_name, p.first_name FROM provider p, appointment
			a
			WHERE p.provider_no=a.provider_no AND a.appointment_no=${appt_no}

