INSERT INTO `eform` VALUES (3,'travel_from_v4','travel_form_v4.html','','2019-04-30','17:01:28','999998',1,'<html>\r\n<head>\r\n    <META http-equiv=\'Content-Type\' content=\'text/html; charset=UTF-8\'>\r\n    <title>Traveller Chart</title>\r\n    <script src=\'${oscar_javascript_path}jquery/jquery-2.2.4.min.js\'></script>\r\n    <script>window.jQuery || document.write(\"\\x3cscript src=\'https://code.jquery.com/jquery-2.2.4.min.js\'\\x3e\\x3c\\/script\\x3e\");</script>\r\n    <!-- import jQuery UI-->\r\n    <script src=\"https://code.jquery.com/ui/1.12.0/jquery-ui.min.js\"></script>\r\n    <!-- import jQuery Theme (ui stylesheet) -->\r\n    <link rel=\"stylesheet\" href=\"https://code.jquery.com/ui/1.12.0/themes/smoothness/jquery-ui.min.css\">\r\n\r\n    <style>\r\n        /* base style for pages */\r\n        .page_container {\r\n            position: relative; \r\n            background: #ffffff;\r\n            float: left;\r\n            border: solid 0;\r\n            margin: 0;\r\n			font-family: \'Helvetica\', \'Arial\', sans-serif;\r\n        }\r\n        /* div containing form input elements */\r\n        .input_elements {\r\n            position: absolute;\r\n            top: 0;\r\n            left: 0;\r\n            width: 100%;\r\n            height: 100%;\r\n        }\r\n        /* base styling for input wrapper divs */\r\n        .gen-widget {\r\n            display: inline-block;\r\n            text-align: left;\r\n            vertical-align: middle;\r\n            background: transparent;\r\n            border: 0;\r\n        }\r\n        /* style wrapped input elements */\r\n        .gen-widget input, textarea {\r\n            /* position: absolute; */\r\n            display: inline-block;\r\n            text-align: left;\r\n            font-weight: bold;\r\n            font-size: 12px;\r\n            font-family: \'Helvetica\', \'Arial\', sans-serif;\r\n            background: transparent;\r\n            color: #000000;\r\n            border: none;\r\n            padding: 0;\r\n            width: 100%;\r\n            height: 100%;\r\n            z-index:10;\r\n            margin: 0;\r\n        }\r\n        /* remove firefox webkit so that checkboxes are resizable */\r\n        .gen-widget input[type=checkbox] {\r\n            -moz-appearance: none;\r\n        }\r\n        /* define xbox styling */\r\n        input.xBox {\r\n            background: #f3f3f3;\r\n            text-align: center;\r\n            font-weight: bold;\r\n            font-size: 11px;\r\n            border: 1px solid black;\r\n            cursor: pointer;\r\n			width: 15px;\r\n			height: 15px;\r\n        }\r\n        /* define initials styling */\r\n        input.initials {\r\n            background: #f3f3f3;\r\n            text-align: center;\r\n            font-size: 10px;\r\n            border: 1px solid black;\r\n            cursor: pointer;\r\n			width: 35px;\r\n			height: 18px;\r\n        }\r\n        input.xBox:focus {\r\n            outline: none;\r\n            color : transparent;\r\n            text-shadow : 0 0 0 #000;\r\n        }\r\n        /* define other */\r\n        .noborder {\r\n	font-size: 120%;\r\n	font-weight: bold;\r\n	color: #000000;\r\n         border-color: transparent !important;\r\n        }\r\n		.bottomborder{\r\nborder-color: transparent;\r\n			border-bottom: 1px solid black !important;\r\n		}\r\n        /* print only styling */\r\n        @media print {\r\n            .DoNotPrint {\r\n                display: none;\r\n            }\r\n            .noborderPrint {\r\n                border-color: transparent !important;\r\n            }\r\n            .page_container {\r\n                page-break-after: always;\r\n            }\r\n        }\r\n\r\n        /* define label styling (only used by generated labels) */\r\n        .label-style_1 {\r\n	        color: #000000;\r\n	        font-size: 12px;\r\n	        font-weight: bold;\r\n	        font-family: Verdana, Arial, sans-serif;\r\n        }\r\n\r\n       